import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [llmResponse, setLlmResponse] = useState('');

  // Wrap fetchSuggestions in useCallback to prevent recreation
  const fetchSuggestions = useCallback(async () => {
    try {
      const res = await axios.post('http://localhost:8000/search', { 
        query, 
        top_k: 5 
      });
      setSuggestions(res.data.results);
    } catch (err) {
      console.error("Search error:", err);
    }
  }, [query]);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (query.length > 2) {
        fetchSuggestions();
      } else {
        setSuggestions([]);
      }
    }, 300);
    
    return () => clearTimeout(timer);
  }, [query, fetchSuggestions]); // Now includes fetchSuggestions in deps

  const handleProductSelect = async (product) => {
    setSelectedProduct(product);
    setQuery(product.title);
    setSuggestions([]);
    setLlmResponse("Loading...");

    try {
      const res = await axios.post('http://localhost:8000/ask_llm', {
        product_id: product.id
      });
      setLlmResponse(res.data.llm_response || "No summary available");
    } catch (err) {
      console.error("LLM Error:", err);
      setLlmResponse(`Error: ${err.message}`);
    }
  };

  return (
    <div className="app">
      <h1>Nike Product Search</h1>
      <div className="search-container">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search products..."
        />
        {suggestions.length > 0 && (
          <ul className="suggestions">
            {suggestions.map((item) => (
              <li key={item.id} onClick={() => handleProductSelect(item)}>
                {item.title} (score: {item.score.toFixed(2)})
              </li>
            ))}
          </ul>
        )}
      </div>

      {selectedProduct && (
        <div className="product-panel">
          <h2>{selectedProduct.title}</h2>
          <div className="llm-response">
            <h3>AI Summary</h3>
            <p>{llmResponse}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
