import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [loading, setLoading] = useState(false);
  const [disableSuggestions, setDisableSuggestions] = useState(false);
  const searchRef = useRef(null);


  // Fetch suggestions
  const fetchSuggestions = useCallback(async () => {
    try {
      const res = await axios.post('http://localhost:8000/search', {
        query,
        top_k: 5
      });
      setSuggestions(res.data.results);
    } catch (err) {
      console.error("Search error:", err);
    }
  }, [query]);

  // Debounced search
  useEffect(() => {
  const handleClickOutside = (event) => {
    if (searchRef.current && !searchRef.current.contains(event.target)) {
      setSuggestions([]); // ✅ Close dropdown
    }
  };

  document.addEventListener("mousedown", handleClickOutside);
  return () => {
    document.removeEventListener("mousedown", handleClickOutside);
  };
}, []);



  // Handle product selection
  const handleProductSelect = async (product) => {
  console.log("🟡 Selected:", product);
  setDisableSuggestions(true);        // ✅ block new suggestions temporarily
  setSuggestions([]);                 // ✅ hide dropdown immediately
  setQuery(product.title);            // ✅ set selected product title
  setLoading(true);
  setSelectedProduct(null);

  try {
    const res = await axios.post('http://localhost:8000/product', {
      product_id: product.id
    });

    console.log("✅ Product details response:", res.data);

    setSelectedProduct({
      ...product,
      title: res.data.title,
      description: res.data.description
    });
  } catch (err) {
    console.error("❌ API error:", err.response?.data || err.message);
  } finally {
    setLoading(false);

    // ✅ Re-enable suggestions AFTER a small delay (optional debounce reset)
    setTimeout(() => {
      setDisableSuggestions(false);
    }, 500);
  }
};





  return (
    <div className="app">
      <h1>Nike Product Search</h1>
      
      {/* Search Bar */}
      <div className="search-container" ref={searchRef}>
  <input
    type="text"
    value={query}
    onChange={(e) => setQuery(e.target.value)}
    placeholder="Search products..."
  />

  {suggestions.length > 0 && (
    <ul className="suggestions">
      {suggestions.map((item) => (
        <li key={item.id} onClick={() => handleProductSelect(item)}>
          {item.title} (score: {item.score.toFixed(2)})
        </li>
      ))}
    </ul>
  )}
</div>


      {/* Product Display - UPDATED SECTION */}
      {loading && <p>Loading product details...</p>}
      
      {selectedProduct && (
        <div className="product-display">
          <h2>{selectedProduct.title}</h2>
          
          {selectedProduct.subtitle && (
            <h3>{selectedProduct.subtitle}</h3>
          )}
          
          <div className="product-description">
            <h4>Description:</h4>
            <p>{selectedProduct.description}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
