import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [llmResponse, setLlmResponse] = useState('');

  // Wrap fetchSuggestions in useCallback to prevent recreation
  const fetchSuggestions = useCallback(async () => {
    try {
      const res = await axios.post('http://localhost:8000/search', { 
        query, 
        top_k: 5 
      });
      setSuggestions(res.data.results);
    } catch (err) {
      console.error("Search error:", err);
    }
  }, [query]);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (query.length > 2) {
        fetchSuggestions();
      } else {
        setSuggestions([]);
      }
    }, 300);
    
    return () => clearTimeout(timer);
  }, [query, fetchSuggestions]); // Now includes fetchSuggestions in deps

  const handleProductSelect = async (product) => {
  setSelectedProduct(product);
  setQuery(product.title);
  setSuggestions([]);
  
  try {
    const res = await axios.post('http://localhost:8000/product', {
      product_id: product.id
    });
    setSelectedProduct({
      ...product,
      description: res.data.description  // Use raw description
    });
  } catch (err) {
    console.error("Failed to fetch product:", err);
  }
};

  return (
    <div className="app">
      <h1>Nike Product Search</h1>
      <div className="search-container">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search products..."
        />
        {suggestions.length > 0 && (
          <ul className="suggestions">
            {suggestions.map((item) => (
              <li key={item.id} onClick={() => handleProductSelect(item)}>
                {item.title} (score: {item.score.toFixed(2)})
              </li>
            ))}
          </ul>
        )}
      </div>

      {selectedProduct && (
  <div className="product-panel">
    <h2>{selectedProduct.title}</h2>
    <div className="description">
      <h3>Product Details</h3>
      <p>{selectedProduct.description}</p>
    </div>
  </div>
)}
    </div>
  );
}

export default App;
